This sample shows how to call StartTranslationAsync with required parameters and request content.

AzureKeyCredential credential = new AzureKeyCredential("<key>");
DocumentTranslationClient client = new DocumentTranslationClient("<Endpoint>", credential);

RequestContent content = RequestContent.Create(new Dictionary<string, object>()
{
    ["inputs"] = new List<object>()
{
new Dictionary<string, object>()
{
["source"] = new Dictionary<string, object>()
{
["sourceUrl"] = "<sourceUrl>",
["filter"] = new Dictionary<string, object>()
{
["prefix"] = "<prefix>",
["suffix"] = "<suffix>",
},
["language"] = "<language>",
["storageSource"] = "AzureBlob",
},
["targets"] = new List<object>()
{
new Dictionary<string, object>()
{
["targetUrl"] = "<targetUrl>",
["category"] = "<category>",
["language"] = "<language>",
["glossaries"] = new List<object>()
{
new Dictionary<string, object>()
{
["glossaryUrl"] = "<glossaryUrl>",
["format"] = "<format>",
["version"] = "<version>",
["storageSource"] = "AzureBlob",
}
},
["storageSource"] = "AzureBlob",
}
},
["storageType"] = "Folder",
}
},
});
Operation operation = await client.StartTranslationAsync(WaitUntil.Completed, content, new ContentType("application/json"));

